/**
 * Helper to build unique IDs for questions based on their source paper
 */
function buildPool(paper: string): Question[] {
  const pool: Question[] = [];
  let source: Partial<Question>[] = [];
  
  switch(paper) {
    case 'Paper I': source = paperOneBase; break;
    case 'Paper II': source = paperTwoBase; break;
    case 'Paper III': source = paperThreeBase; break;
    case 'Paper IV': source = paperFourBase; break;
    case 'Paper V': source = paperFiveBase; break;
  }
  
  source.forEach((q, i) => {
    pool.push({
      id: `${paper.replace(/\s+/g, '')}-${i}`,
      text: q.text!,
      options: q.options!,
      correctAnswer: q.correctAnswer!,
      rationale: q.rationale!
    });
  });
  
  return pool;
}

/**
 * Public getters for paper pools
 */
export function getPaperQuestionsPool(paper: string): Question[] {
  return buildPool(paper);
}
